mapper.select:
select a.* 
from (
	select rownum r, b.* 
	from (


			select rst.* 
			from restaurant rst, delivery_zone dz, v_rst_rating vrr 
			where rst.rst_id = vrr.rst_id and rst.rst_id = dz.rst_id and sido = #{sido} and sigungu = #{sigungu} and bname = #{bname}

				and category_id = #{category_id}

			order by avg_rating desc) b) a


			select rst.* 
			from restaurant rst, delivery_zone dz, v_rst_review_count vrrc 
			where rst.rst_id = vrrc.rst_id and rst.rst_id = dz.rst_id and sido = #{sido} and sigungu = #{sigungu} and bname = #{bname}

				and category_id = #{category_id}

			order by review_count desc) b) a


			select rst.* 
			from restaurant rst, delivery_zone dz 
			where rst.rst_id = dz.rst_id and sido = #{sido} and sigungu = #{sigungu} and bname = #{bname}

				and category_id = #{category_id}

			order by min_order asc) b) a


			select rst.* 
			from restaurant rst, delivery_zone dz 
			where rst.rst_id = dz.rst_id and sido = #{sido} and sigungu = #{sigungu} and bname = #{bname}) b

				and category_id = #{category_id}

			) a 


where r >= #{start} and r <= #{end}

select if(count(*) = 1, 1, 0) 
from favorite_restaurant 
where email = #{email} and rst_id = #{rst_id}

mapper.delete:
delete 
from favorite_restaurant 
where email = #{email} and rst_id = #{rst_id}

mapper.select:
select avg_rating 
from v_rst_rating 
where rst_id = #{rst_id}

select count(r.rst_id) 
from restaurant r, delivery_zone d 
where r.rst_id = d.rst_id and sido = #{sido} and sigungu = #{sigungu} and bname = #{bname}

	and rst_name like '%'||#{searchText}||'%'


	and category_id = #{category_id}
